import React, {useState} from 'react';
// import {useDispatch} from 'react-redux';
// import { loginUser } from '../../../_actions/user_action';
// import { withRouter } from 'react-router-dom';
import { Grid, Paper, Avatar, TextField, FormControlLabel, Checkbox, Button, Typography, Link } from '@material-ui/core';


const LoginPage = (props) => {

    // const dispatch = useDispatch();

    const [UserId, setUserId] = useState("");
    const [Password, setPassword] = useState("");

    const onUserIdHandler = (e) => {
        setUserId(e.target.value);
    }
    const onPasswordHandler = (e) => {
        setPassword(e.target.value);
    }
    const onSubmitHandler = (e) => {
        e.preventDefault();
        let body = {
            userId : UserId,
            password : Password,
        }
        console.log(body);
    }

    const paperStyle = {
        padding : 20,
        height : '70vh',
        width : 280,
        margin : '20px auto',
    }
    const avatarStyle = {
        backgroundColor : 'green',
    }
    const btnstyle = {
        margin : '8px 0'
    }

    return (
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align="center">
                    <Avatar style={avatarStyle}></Avatar>
                    Sign in

                </Grid>
                <TextField label="UserId" placeholder="Enter userId" fullWidth required/>
                <TextField label="Password" placeholder="Enter Password" type="password" fullWidth required/>
                <FormControlLabel 
                    control={
                        <Checkbox
                            name="checkedB"
                            color="primary"
                        />
                    }
                    label="Remember Me"
                />
                <Button type="submit" color="primary" style={btnstyle} variant="contained" fullWidth>Sign in</Button>
                <Typography>
                    <Link href="#">
                        Forgot password ?
                    </Link>
                </Typography>
                <Typography> Do you have an acoount?
                    <Link href="#">
                        Sign up
                    </Link>
                </Typography>
            </Paper>
        </Grid>
        // <div>
        //     <form onSubmit={onSubmitHandler}>
        //         <label>UserId</label>
        //         <input type="text" value={UserId} onChange={onUserIdHandler} />
        //         <label>Password</label>
        //         <input type="password" value={Password} onChange={onPasswordHandler} />
        //         <br />
        //         <button type="submit">
        //             login
        //         </button>
        //     </form>
        // </div>

    )
}

export default LoginPage;
