import React, { useRef, useState } from 'react'
import { useDispatch } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { updateUser } from "../../../api/actions/user_action";
import { Button } from '@material-ui/core';

const UpdateUser = (props, {UserData}) => {
    const dispatch = useDispatch();
    console.log('userdata', UserData);
    const [userNick, setuserNick] = useState(UserData.userNickName);
    const [userMessage, setuserMessage] = useState(UserData.message);
    const {Input1, Input2} = "";
    // const inputEl1 = useRef(UserData.userNickName);
    // const inputEl2 = useRef(UserData.message);

    const onSubmitHandler = (e) => {
        e.preventDefault();
        let body = {
            userNickName: Input1,
            message: Input2
        }

        dispatch(updateUser(body))
        .then(response => {
            if(response.payload.updateUser){
                props.history.push('/main');
            }else{
                alert('Error');
            }
        })
    }

    const btnstyle ={
        margin : '18px 0',
        fontWeight : 'bolder',
        color : '#fff',
        backgroundColor : '#845460'
    }

    return (
        <div className="update_user">
            <div>프로필 수정</div>
            <form onSubmit={onSubmitHandler}>
                <img src={UserData.image} />
                {/* <input type="text" placeholder={userNick} value={Input1} ref={inputEl1}/>
                <input type="text" placeholder={userMessage} value={Input2} ref={inputEl2}/> */}
                <input type="text" placeholder={userNick} value={Input1}/>
                <input type="text" placeholder={userMessage} value={Input2}/>
                <Button type="submit" style={btnstyle} variant="contained" fullWidth>수정 완료</Button>
            </form>
        </div>
    )
}

export default withRouter(UpdateUser);
