import React, {useState} from 'react';
import {useDispatch} from 'react-redux';
import { registerUser } from '../api/actions/user_action';
import { withRouter } from 'react-router-dom';
import { Grid, Paper, Avatar, FormControlLabel, Checkbox, TextField, Button, Typography } from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { Link } from 'react-router-dom';

const LoginPage = (props) => {

    const dispatch = useDispatch();

    const [UserId, setUserId] = useState("");
    const [Password, setPassword] = useState("");
    

    const onUserIdHandler = (e) => {
        setUserId(e.target.value);
    }
    const onPasswordHandler = (e) => {
        setPassword(e.target.value);
    }
    const onSubmitHandler = (e) => {
        e.preventDefault();
        let body = {
            userId : UserId,
            password : Password,
        }

        // dispatch(loginUser(body))
        // .then(response => {
        //     if(response.payload.loginSuccess){
        //         props.history.push('/');
        //     }else{
        //         alert('Error');
        //     }
        // })

    }

    const paperStyle ={
        padding : 20,
        height : '70vh',
        width : 280,
        margin : '20px auto',
        textAlign : 'center',
        // background : '#f6bd63',
    }
    const spanStyle ={
        
    }
    const btnstyle ={
        margin : '8px 0',
        fontWeight : 'bolder',
        color : '#fff',
        backgroundColor : '#845460'
    }
    const inputstyle ={
        width : '90%',
        margin : '0 auto',
        height : '30px',
        marginBottom : '20px',
        background : '#855460',
        color : '#fff',
    }

    return (
        <Grid>
            <Paper elevation={10} style={paperStyle}>
                <Grid align="left">
                    <Typography>
                        <Link to="/">
                            <ArrowBackIosIcon />
                        </Link>
                        <span style={spanStyle}>회원가입</span>
                    </Typography>
                </Grid>
                <form onSubmit={onSubmitHandler}>
                    <input style={inputstyle} type="text" label="UserId" placeholder="Enter UserId" value={UserId} onChange={onUserIdHandler} required/>
                    <input style={inputstyle} type="password" label="Password" placeholder="Enter Password" value={Password} onChange={onPasswordHandler} required/>
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="모두 동의합니다"
                    />
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="Remember Me"
                    />
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="Remember Me"
                    />
                    <Button type="submit" style={btnstyle} variant="contained" fullWidth>Sign up</Button>
                </form>
            </Paper>
        </Grid>
    )
}

export default withRouter(LoginPage);
