import React, {useState} from 'react';
import {useDispatch} from 'react-redux';
import { registerUser } from '../api/actions/user_action';
import { withRouter } from 'react-router-dom';
import { Grid, Paper, Avatar, FormControlLabel, Checkbox, TextField, Button, Typography } from '@material-ui/core';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import { Link } from 'react-router-dom';

const LoginPage = (props) => {

    const dispatch = useDispatch();

    const [UserId, setUserId] = useState("");
    const [Password, setPassword] = useState("");
    

    const onUserIdHandler = (e) => {
        setUserId(e.target.value);
    }
    const onPasswordHandler = (e) => {
        setPassword(e.target.value);
    }
    const onSubmitHandler = (e) => {
        e.preventDefault();
        let body = {
            userId : UserId,
            password : Password,
        }

        // dispatch(loginUser(body))
        // .then(response => {
        //     if(response.payload.loginSuccess){
        //         props.history.push('/');
        //     }else{
        //         alert('Error');
        //     }
        // })

    }

    const paperStyle ={
        padding : 20,
        height : '800px',
        width : 280,
        margin : '20px auto',
        // textAlign : 'center',
        position: 'absolute',
        top:'50%',
        left:'50%',
        transform: 'translate(-50%, -50%)'
    }
    const spanStyle ={
        
    }
    const hrstyle ={
        backgroundColor : 'gray',
        height : '1px'
    }
    const textStyle1 ={
        width : 150,
    }
    const btnstyle ={
        margin : '8px 0',
        fontWeight : 'bolder',
        color : '#fff',
        backgroundColor : '#845460'
    }
    const btnstyle2 ={
        margin : '8px 0',
        fontWeight : 'bolder',
        color : '#fff',
        backgroundColor : '#f6bd63',
        width : '80px',
        height : '40px',
        borderRadius : '15px 15px'
    }

    return (
        <Grid>
            <Paper elevation={10} style={paperStyle} >
                <Grid align="left">
                    <Typography>
                        <Link to="/">
                            <ArrowBackIosIcon />
                        </Link>
                        회원가입
                    </Typography>
                </Grid>
                <hr style={hrstyle}/>
                <form onSubmit={onSubmitHandler}>
                    <TextField style={textStyle1} id="standard-basic" label="아이디" type="text" value={UserId} onChange={onUserIdHandler} />
                    <button type="submit" style={btnstyle2}>중복확인</button>
                    <TextField id="standard-basic" label="비밀번호" type="password" value={Password} onChange={onPasswordHandler} />
                    <TextField id="standard-basic" label="비밀번호확인" type="password" value={Password} onChange={onPasswordHandler} />
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="모두 동의합니다"
                    />
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="이용약관 동의"
                    />
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="개인정보 취급방침 동의"
                    />
                    <FormControlLabel 
                        control={
                            <Checkbox
                                name="checkedB"
                                color="primary"
                            />
                        }
                        label="마케팅 정보 수신 동의"
                    />
                    <Button type="submit" style={btnstyle} variant="contained" fullWidth>Sign up</Button>
                </form>
            </Paper>
        </Grid>
    )
}

export default withRouter(LoginPage);
