import React from 'react';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import usePromise from '../../../../usePromise';
import { LoadingOutlined } from '@ant-design/icons';
import NewsItem from './NewsItem';

const News = ({ UserData }) => {

    const [loading, resolved, error] = usePromise(() => {
        console.log('기사수집')
        return axios.get(
            `https://newsapi.org/v2/top-headlines?country=kr&apiKey=112d8e8b89e94be1a897eaffad725b6a`
        );
    }, [UserData]);

    // 대기중 일때 실행
    if (loading) {
        return (
            <LoadingOutlined />
        )
    }
    // 값이 없으면 null 값으로 반환
    if (!resolved) {
        return (
            <div>뉴스오류</div>
        );
    }

    // resolved값이 유효할 때
    const { articles } = resolved.data;

    return (
        <div>
            {articles.map((article)=>{
                <NewsItem key={article.key} article={article}/>
            })}
        </div>
    )

    // // 데이터를 그대로 NewsItem파일로 전송해준다
    // return ( // article 값이 유효하면
    //     <NewsListBlock>
    //         {articles.map((article) => (
    //             <NewsItem key={article.url} article={article} />
    //         ))}
    //     </NewsListBlock>
    // )

}


export default withRouter(News);
